import { useState, useCallback } from "react"

export default function Sidebar({ initialMenuItems }) {
  // State for new item input
  const [newMenuItem, setNewMenuItem] = useState("")

  // TODO 2: State for menu items
  const [menuItems, setMenuItems] = useState(initialMenuItems || [])

  // State for filter input
  const [filter, setFilter] = useState("")

  // TODO 3: Add menu item callback
  const addMenuItem = useCallback(() => {
    if (newMenuItem.trim() !== "") {
      setMenuItems([...menuItems, newMenuItem])
      setNewMenuItem("") // Clear input after adding
    }
  }, [menuItems, newMenuItem])

  // TODO 4: Filter menu items (case-insensitive)
  const filteredItems = menuItems.filter((item) =>
    new RegExp(filter, "i").test(item)
  )

  // TODO 1: Render menu items
  return (
    <div>
      {/* Input to add new menu item */}
      <input
        type="text"
        id="newMenuItemValue"
        value={newMenuItem}
        onChange={(event) => setNewMenuItem(event.target.value)}
        placeholder="New menu item"
      />
      <br />
      <button onClick={addMenuItem}>Add Item</button>
      <br />
      {/* Input to filter menu items */}
      <input
        id="filter"
        type="text"
        value={filter}
        onChange={(event) => setFilter(event.target.value)}
        placeholder="Filter by..."
      />
      <br />
      {/* Render filtered menu items as a list */}
      <ul>
        {filteredItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  )
}
